<template>
  <div class="">
		<div class="card shadow-lg">
		<div class="mt-3 text-center "><h3 class="">Sign In</h3></div>
		<div class="card-body">
        <form @submit.prevent="handleSubmit">
            <div class="form-group">
                <label for="username" class="lab">Username</label>
                <input type="text" v-model="username" name="username" class="form-control" :class="{ 'is-invalid': submitted && !username }" autofocus placeholder="Enter e-mail"/>
                <div v-show="submitted && !username" class="invalid-feedback text-danger">Username is required</div>
            </div>
            <div class="form-group">
                <label htmlFor="password" class="lab">Password</label>
                <input type="password" v-model="password" name="password" class="form-control" :class="{ 'is-invalid': submitted && !password }" placeholder="Enter password"/>
                <div v-show="submitted && !password" class="invalid-feedback text-danger">Password is required</div>
            </div>
            <div class="form-group">
                <button class="btn btn-primary btsg" :disabled="status.loggingIn">Login</button>
                <img v-show="status.loggingIn" src="data:image/gif;base64," />
                <router-link to="/register" class="btn btn-link">Register</router-link>
            </div>
        </form>
		</div>
		</div>
  </div>
</template>

<script>
import { mapState, mapActions } from 'vuex'

export default {
    data () {
        return {
            username: '',
            password: '',
            submitted: false
        }
    },
    computed: {
        ...mapState('account', ['status'])
    },
    created () {
        // reset login status
        this.logout();
    },
    methods: {
        ...mapActions('account', ['login', 'logout']),
        handleSubmit (e) {
            this.submitted = true;
            const { username, password } = this;
            if (username && password) {
                this.login({ username, password })
            }
        }
    }
};
</script>

<style lang="css">
  input::placeholder {
    color: black !important;
    text-transform: lowercase !important;
    font-size: .8rem;
    font-style: italic;
    font-family: cursive;
  }


</style>
